#include<iostream>
#include<stack>
using namespace std;

class Solution {
public:
    int longestValidParentheses(string s) {
        int length = s.size();
        stack<int> mystack;
        int i,sol;
        int ans = 0;
        
        for(i=0; i<length; i++)
        {
                 if(s[i] == '(')
                 {
                         mystack.push(i);
                 }
                 else if(s[i] == ')')
                 {
                      //cout<<i<<" "<<ans<<" "<<mystack.size()<<endl;
                      if(!mystack.empty() && s[mystack.top()] == '(')
                      {
                                          mystack.pop();
                                          //cout<<"test:"<<i<<" "<<ans<<" "<<mystack.size()<<endl;
                                          if(mystack.empty()) 
                                          {
                                                              sol = i+1;
                                          }
                                          else
                                          {
                                              sol = i - mystack.top();
                                          }
                                          
                                          ans = max(ans, sol);                  
                      }
                      else mystack.push(i);
                 }         
        }
        
        return ans;
    }
};


